import Cookie from "js-cookie";
export default {
    state: {
        isCollapse: false,      /*控制菜单展开还是收起*/
        menu:[]
    },

    mutations: {
        collapseMenu(state) {
            state.isCollapse = !state.isCollapse
        },

        // 设置菜单
        setMenu(state, val) {
            state.menu = val
            // 存入缓存
            Cookie.set('menu',JSON.stringify(val))
        },

        addMenu(state,val){
            
        }

    }

}
